package sample_code;

public class WhileDoWhile {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//question 3&4
		int i=10;
		while(i>=1)
		{
			System.out.println(i);
			i--;
		}
		System.out.println("--------------------------");
		 int j=10;
	        do
	        {
	            System.out.println(j);
	            j--;
	        }
	        while(j>0);
	        System.out.println("----------------------");
	        int k=10;
	        while(k>=1)
	        {
	        	System.out.println("Hello world");
	        	k--;
	        }
	}

}
